CREATE FUNCTION [FnFormatNumber] (@Number int, @digit int)
RETURNS Varchar(256)
 
AS
BEGIN
	Declare @R Varchar(256)
	Set @R = ''

	while (len(@Number) < @digit )
	begin
		Set @R = @R+'0'
		Set @digit = @digit -1
	end
	
	Set @R = @R + Cast(@Number as Varchar(256))

	RETURN @R
END

