			@biQty3 Float
	
	DECLARE @cursor_Name CURSOR 
	Set @cursor_Name = CURSOR FAST_FORWARD FOR 

	SELECT 
		D.MatGuid,
		[B].[StoreInGuid],
		[B].[StoreOutGuid],
		[d].[Qty], 
		[d].[Qty2], 
		[d].[Qty3]	From
