RETURNS Varchar(256)
 
AS
BEGIN
	Declare @R Varchar(256)
	Set @R = ''

	while (len(@Number) < @digit )
	begin
		Set @R = @R+'0'
		Set @digit = @digit -1
	end
	
	Set @R = @R + @Number

	RETURN @R
END

