		and (
				([Area] = @Area1 and @Area2 = 0)
				or (@Area1 = 0)
				or (@Area1 <> 0 and @Area2 <> 0)
			)
		and ([Price] between @Price1 and @Price2 or @Price2 = 0)
		and (
				([Price] = @Price1 and @Price2 = 0)
				or (@Price1 = 0)
				or (@Price1 <> 0 and @Price2 <> 0)
