		and (
				([OfferType] = 1 and [OfferState] = 0 and @CkSale = 1 )
				or ([OfferType] = 0)
				Or ([OfferState] = 1)
				or (@CkSale <> 0)
			)

		and (
				([OfferType] = 1 and [OfferState] = 1 and @CkNotSale = 1 )
				or ([OfferType] = 0)
				Or ([OfferState] = 0)
				or (@CkNotSale <> 0)
