	From
		#R R
		inner join ChecksAccountDetail D on D.[ParentGuid] = R.Guid and Kind = 1

	Select *, 0 as [Operation] from #R
	Order By
		[DueDate] 

	Select 
		[TypeName],
		Count(*) As [Count],
		Sum([Value] * [CurrencyVal])  as [Total]
	From
		#R
	Group By
		[TypeName]
		


