									p.CustomerGuid = l.CustomerGuid
									and P.ParkingGuid = l.ParkingGuid
									and P.Guid <> l.Guid
								Order by
									FromDate desc
								)
		From
			[ParkingContract]  L
		where
			L.Guid = @ContractGuid or @ContractGuid = 0x0
			
		ALTER TABLE [ParkingContract] Enable TRIGGER [TRG_Trace_ParkingContract]
	end
		

