Create Function dbo.FnStrToAscii(@Str  Varchar(256))
returns bigint  begin
	Set @str = left(@Str, 6)
	
	Declare @R Varchar(256)
	Set @R = 0	
	Declare @I int

	Set @I = 0
	while @I < Len(@Str)
	begin
		Set @R = @R + Cast(
						(
							Ascii(SUBSTRING (@Str, @I+1, 1))
						)
						as Varchar(5))

		Set @I = @I + 1

	end
	
	return Cast(@R as bigint)
end

