	--return
	
	
	
	Declare @Date Datetime
	Set @Date = dbo.fnHijriStrtoGergDate(Cast(@HYear as Varchar(4))+'/1/1','yyyy/mm/dd')
	Declare @S Varchar(256)

	Declare @Hy int
	Declare @Hm int
	Declare @Hd int

	Set @S = Convert(Varchar(256), @Date , 131)

	Set @Hy = SUBSTRING (@S, 7, 4)
		
	Declare @iM int, @iD int, @MXD int
	Set @Im = 1
	while @iM <= 12
	begin
		Set @iD = 1
		Set @MXD = 1
		while @iD <= 30
		begin
