			Set @Hy = SUBSTRING (@S, 7, 4)
			Set @Hm = SUBSTRING (@S, 4, 2)
			Set @Hd = SUBSTRING (@S, 1, 2)

			Set @hd = Replace(@hd,' ','0')
			Set @hm = dbo.FnFormatNumber(@hm, 2)
			Set @hd = dbo.FnFormatNumber(@hd, 2)
			
			if @MXD < @hd
			Set @MXD = @hd			
			--Select @Im, @Id, @Hy, @Hm, @Hd

			Set @iD = @iD + 1
		end
		
		insert into HjrConfig
