*/

	--CREATE INDEX IXS_Guid ON #Res ([Guid])
	-----------------------------------------------------------------------------------
	CREATE INDEX IXS_Res_Path ON #Res ([Path], [Type], [Code])
	Alter Table #Res add Id int identity(1,1)
	
