CREATE FUNCTION [FnStrToTable] (@Number Varchar(8000))
RETURNS @Res Table ([Str] Varchar(256))  AS
BEGIN
	Declare @Str Varchar(256)
	Set @Str = ''

	Declare @I int 
	Set @I = 0

	while (@i <= len(@Number) )
	begin
		if SubString(@Number, @I , 1) = ','
		begin
			insert into @Res
			Select @Str

			Set @Str = ''
		end
		else
		begin
			Set @Str = @Str + SubString(@Number, @I , 1)
		end

		Set @I = @I + 1
	end

	if @Str <> ''
	insert into @Res
	Select @Str
	
	RETURN
END

