			)

			or
			(
				([L].[ContractFinishDate] between @Date1 and @Date2 and @Datewith = 2)
				and ([L].[ContractFinish] = 1)
			)

			or
			(
				([L].[EditDate] between @Date1 and @Date2 and @Datewith = 3)
