		(Select Sum([Area]) From @Tbl where [State] = dbo.sc('ãÄÌÑÉ')) as [RentArea],
		(Select Sum([Area]) From @Tbl where [State] = dbo.sc('ÛíÑ ãÄÌÑÉ')) as [NotRentArea]




