		Inner join [Resource] [R] on [R].[Guid] = [B].[Guid] and [spid] = @@Spid
		
		left join [#LeaseApartment] [L] On [A].[Guid] = [L].[ApartmentGuid] and [L].[LeaseKind]  = 1 --and ([L].[ContractFinish] = 0) /*19/8/2014*/
		
