	FROM
		[vwLeaseApartment]  C
		inner join [vwContractType] T on T.Guid = C.TypeGuid
	WHERE
		C.Guid = @ContractGuid

	
	Select 
		@EntryNote  =				REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(
									REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(
									@EntryNote, '[N]', TypeNameAr),
									'[M]', TypeNameLtn),
									'[A]', CustArName),
									'[B]', CustltnName),
									'[C]', FlatNo),
									'[D]', BuildingArName),
									'[E]', BuildingLtnName),
