						Set @DetailNum = @DetailNum + 1
						Insert into [DEntry]
		 				([ParentGuid],[Number],[AcGuid],[Debit],[Credit],[CurrencyGuid],[CurrencyVal],[CostGuid],[ObverseAcGuid],[Note])
