	update #R_CheckReturn
	Set
		CollectValue = S.value
	From
		#R_CheckReturn C
		inner join #ChecksPartialCollection_S S on S.CheckGuid = C.Guid
		
	update #R_CheckReturn Set RestValue = value - isNull(CollectValue,0)		


