			[C11].[LtnName] as [Note_DebitAccountLtnName],
			[C22].[Code] as [Note_CreditAccountCode],
			[C22].[ArName] as [Note_CreditAccountArName],
			[C22].[LtnName] as [Note_CreditAccountLtnName],

