		if @ContractKind = 4
		begin
			Insert into [ServicesContractCachPayment] ([ContractGuid], [EntryGuid])
			Select @ContractGuid, @Guid

		    if exists(Select * from [ServicesContractCachPayment] where [EntryGuid] = @Guid)
