		,[A].[Area] as [FlatArea]
		,Case when ([L].[ToDate] <= @Now_Date) or ([L].[ContractFinish] = 1) then dbo.sc('ãäÊåí ÇáãÏÉ')  
			  when [L].[ToDate] > @Now_Date then dbo.sc('ÛíÑ ãäÊåí ÇáãÏÉ') end as [ContractState]
		,[B].[Emirate] as [Emirate]
		,[B].[Suburb] as [Suburb]
		,[B].[Area] as [Area]
