				@SecLvl,
				@EnNum,
				@Mark,
				@Date,
				@CurrencyGuid,
				@CurrencyVal,
				'',
				1660 + @Kind,
				@BranchGuid,
				@AutoPostedEntry
				
			Declare @DetailNum Int
			Set @DetailNum = 0

			Declare @CheckValue Float,
					@CommValue Float

