			if (not (@Kind = 1 and isNull(@CollectedEntryTypeGuid, 0x0) <> 0x0)) --Ýí ÍÇá áã íÊã ÊæáíÏ ÓäÏ ÞÈÖ
				and (@OperationCreateEntry = 1) 
