		,[Ac2].[Code]+'-'+[Ac2].[Name] COLLATE database_default as [AccountName]
		,[Ac].[Level]
		,isnull([Ac].[Path],'') as [Path]
		,[Ac2].[Guid]
		,[Ac2].[ParentGuid]
		,Case when Type = 0 then [Ac2].[FinalGuid] 
