						Select 
							[CheckGuid],
							Sum([Value]) as [Value] 
						From
							[ChecksPartialCollection] 
						Group By
							[CheckGuid]
					) [C4] on [C4].[CheckGuid] = [P].[Guid]

  	where
 		([P].[DueDate] Between @Date1 and @Date2 or @CheckDueDate = 0)


