		CLOSE cursor_Name
		DEALLOCATE cursor_Name
		
		
		Alter Table [LeaseApartment] Enable Trigger All

	
	exec PrcCheckIndex '', 'LeaseApartment', 1
	
	Select 
		@RC as [RowCount],
		(Select Count(*) from #T_RNConmp_contra) as FailCount


