					and m.StoreGuid = m2.StoreGuid
			) as [Balance]
			,Case when ([btInOut] = -1) and (Kind <> 2) then ([TotalPrice] - Discount + Extra) - (avgPrice * (QtyOut - QtyIn )) end AS Profit
		from 
			#MatMove m
		Order By
			[budate], Kind, [btInOut] * -1 , [BuNumber], [MatCode]	

