Create Procedure [PrcCreateEntrytypeChecksPartialCollection]
(
	@CollectionGuid uniqueidentifier = '383C4089-ADC3-470B-80C5-3042CA07F0EE',
	@CheckGuid uniqueidentifier = 'BA31BD68-DB22-42EA-BFA8-4E8EF4235659',
