Create Procedure [PrcCreateContractLandVillEntry]
(
	@ContractGuid uniqueidentifier = 'CC0F55C4-1A38-4604-870D-F84B4B7F036A',
	@MaxDate int = 42644
)
 
AS
	Set NoCount on 
	Declare 
			@EnNum int,
			@EnGuid [uniqueidentifier]

	-- ÇáÞíÏ ÇáÓÇÈÞ
	Select 
		@EnNum = isnull([Number],0)
		,@EnGuid = isnull([Guid],0x0)
	From
		[Hentry]
	where
